import { createSlice } from "@reduxjs/toolkit";

const loadingSlice = createSlice({
  name: "loadingSlice",
  initialState: {
    isLoading: true,
  },
  reducers: {
    setIsLoading(state, action) {
      state.isLoading = true;
    },
  },
});

export const { setIsLoading } = loadingSlice.actions;

export default loadingSlice.reducer;
