import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Route, Routes } from "react-router-dom";
import DetailsPage from "./pages/DetailsPage";

import HomePage from "./pages/HomePage";
import getCountriesFromApi from "./redux/countries-action";
import { setIsLoading, setIsNotLoading } from "./redux/loading-slice";

function App() {
  const dispatch = useDispatch();
  const countries = useSelector((state) => state.country.countries);
  const isLoading = useSelector((state) => state.loading.isLoading);

  useEffect(() => {
    dispatch(setIsLoading(true));
    dispatch(getCountriesFromApi());
    dispatch(setIsNotLoading(false));
  }, [dispatch]);

  if (isLoading) {
    return <h2>Loading</h2>;
  }

  return (
    <Routes>
      <Route path="/" element={<HomePage countries={countries} />} />
      <Route path="/:countryName" element={<DetailsPage />} />
    </Routes>
  );
}

export default App;
